package ejercicio4;

public class Ejercicio4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int N = 40;
		System.out.println("Valor de N: " + N);

		
		N += 77; 
		
		System.out.println("Valor de N despues del incremento: " + N);
		
		N -= 3; 
		System.out.println("Valor de N despues de decrementar: "+ N);
		
		N*= 2; 
		
		System.out.println("Valor de N despues de duplicar: " + N);
	}

}
